import React, {useState} from 'react'
import {nanoid} from 'nanoid'
import TodoItems from './TodoItems'
export default function TodolistDlt() {
const [task, setTask] = useState("")
const [arr, setArr] = useState([])


    const handleChange =(el)=>{
    
        setTask(el.target.value)
       
    }

 
    const handleClick=()=>{
        let objArr = {title:task, id:nanoid(),status:"1"}
        setArr([objArr, ...arr])

    }
    const handleDlt =(id)=>{
        let newArr = arr.filter((item)=>item.id !=id)
        setArr(newArr)
    }

    
  return (
    
     <>
     <div className='con'>
         <input type="text" placeholder='Enter ToDo Item' onChange={handleChange} />
         <button onClick={handleClick}>Submit</button>
    </div>
       {arr.map((item)=>{

           return <TodoItems key={item.id}{...item} handleDlt={handleDlt}  />
       })}

     </>
    

  )
}
